  username     VARCHAR(32)                           NOT NULL
  COMMENT '用户名',
  ip           VARCHAR(20)                           NOT NULL
  COMMENT '登录IP',
  is_deleted   TINYINT                               NOT NULL                    DEFAULT 0
  COMMENT '逻辑删除:{0:未删除, 1:已删除}',
  created_time TIMESTAMP                             NOT NULL                    DEFAULT CURRENT_TIMESTAMP
  COMMENT '创建时间',
